def solve132(s):
    is_palindrome = {}
    for i in range(len(s)):
        is_palindrome[i,i] = True
        x = i-1
        y = i
        while x >= 0 and y < len(s):
            if s[x] == s[y]:
                is_palindrome[x,y] = True
                x -= 1
                y += 1
            else:
                break
        x = i
        y = i+1
        while x >= 0 and y < len(s):
            if s[x] == s[y]:
                is_palindrome[x,y] = True
                x -= 1
                y += 1
            else:
                break
        x = i-1
        y = i+1
        while x >= 0 and y < len(s):
            if s[x] == s[y]:
                is_palindrome[x,y] = True
                x -= 1
                y += 1
            else:
                break
    dp = [10**9 for i in range(len(s))]
    dp[0] = 0
    for i in range(1, len(s)):
        if (0,i) in is_palindrome:
            dp[i] = 0
            continue
        for j in range(i):
            if (j+1, i) in is_palindrome:
                dp[i] = min(dp[i], dp[j]+1)
            else:
                dp[i] = min(dp[i], dp[j]+1+(i-j))
    return dp[len(s)-1]




