def solve(target, s, stations):
    stations.append([target, 0])
    n = len(stations)
    q = [-1 for _ in range(n)]
    dp = [[-1 for _ in range(n)] for _ in range(n)]
    t = []
    for i in range(n):
        for j, it in t:
            q[j] = max(q[j], it)
        t = []
        for j in range(i+1):
            if j == 0:
                if s >= stations[i][0]:
                    dp[i][j] = s - stations[i][0]
            else:
                # for a in range(i):
                #     if dp[a][j-1] >= 0 and dp[a][j-1] + stations[a][1] >= stations[i][0] - stations[a][0]:
                #         # print(i,j,a,j-1,dp[a][j-1],stations[a][1])
                #         dp[i][j] = max(dp[i][j], dp[a][j-1] + stations[a][1] - (stations[i][0] - stations[a][0]))
                it = q[j-1]
                if it >= 0:
                    # print(i,j,it)
                    if it >= 0 and it >= stations[i][0] :
                        dp[i][j] = max(dp[i][j], it - stations[i][0])
            if dp[i][j] >= 0:
                t.append((j, dp[i][j] + stations[i][1] + stations[i][0]))

    # print(dp)
    rs = 10**9
    for j in range(n):
        if dp[n-1][j] >= 0:
            rs = min(rs, j)
    if rs == 10**9:
        return -1
    return rs


